import './UserInput.scss'
const userInput = () => {
const submitHandler = (event) =>{
    event.preventDefault();
    console.log('Submit');

}
const resetHandler = (event) =>{
    console.log('Reset');
}
const changeHandler = (input,value) =>{
    console.log(input,value);
}

    return (
    <form className="form" onSubmit={submitHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={(event) => changeHandler('current-savings',event)} />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={(event) => changeHandler('yearly-contribution',event.target.value)} />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange={(event) => changeHandler('expected-return',event.target.value)} />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={(event) => changeHandler('duration',event.target.value)}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" onClick={resetHandler} className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>);
};

export default userInput;
